import React, { useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Autoplay, Pagination } from "swiper/modules";
import "swiper/css";
import "swiper/css/pagination";
import './index.css';
import { mockCompanies, mockCandidates, mockBets, adminCarouselCompanyIds } from './mockData';
import { mockUsers } from './mockUsers';
import PageLayout from "./PageLayout";

const defaultUser = mockUsers[0]; // Jon Snow

const filterOptions = [
  { key: 'all', label: 'All', icon: (
    <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round" strokeLinejoin="round" className="w-8 h-8"><circle cx="12" cy="12" r="11" fill="none" stroke="none" /><polygon points="10,8 18,12 10,16" fill="currentColor" stroke="none" /></svg>
  ) },
  { key: 'quant', label: 'Quant', icon: (
    <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round" strokeLinejoin="round" className="w-8 h-8"><rect x="6" y="4" width="12" height="16" rx="2" /><circle cx="9" cy="8" r="1" /><circle cx="15" cy="8" r="1" /><rect x="9" y="12" width="6" height="2" rx="1" /></svg>
  ) },
  { key: 'sde', label: 'SDE', icon: (
    <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round" strokeLinejoin="round" className="w-8 h-8"><polyline points="16 18 22 12 16 6" /><polyline points="8 6 2 12 8 18" /></svg>
  ) },
  { key: 'product', label: 'Product', icon: (
    <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round" strokeLinejoin="round" className="w-8 h-8"><rect x="3" y="7" width="18" height="10" rx="2" /><rect x="7" y="11" width="10" height="2" rx="1" /></svg>
  ) },
  { key: 'data', label: 'Data', icon: (
    <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round" strokeLinejoin="round" className="w-8 h-8"><ellipse cx="12" cy="7" rx="8" ry="3" /><path d="M4 7v10c0 1.66 3.58 3 8 3s8-1.34 8-3V7" /></svg>
  ) },
  { key: 'core', label: 'Core', icon: (
    <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round" strokeLinejoin="round" className="w-8 h-8"><rect x="4" y="4" width="16" height="16" rx="4" /><path d="M8 8h8v8H8z" /></svg>
  ) },
  { key: 'misc', label: 'Misc', icon: (
    <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round" strokeLinejoin="round" className="w-8 h-8"><circle cx="6" cy="12" r="2" /><circle cx="12" cy="12" r="2" /><circle cx="18" cy="12" r="2" /></svg>
  ) },
];

export default function HomePage({ showAnnouncement, setShowAnnouncement, announcementIdx, announcements, isSliding }) {
  const [selectedDomain, setSelectedDomain] = useState('all');
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCompany, setSelectedCompany] = useState(null);

  // Carousel logic: always show 8 fixed companies selected by admins
  const carouselCompanies = adminCarouselCompanyIds.map(id => mockCompanies.find(c => c.id === id)).filter(Boolean);
  const carouselDisplay = [...carouselCompanies.slice(0, 8)];
  while (carouselDisplay.length < 8) carouselDisplay.push(null); // Fill with placeholders

  // Filter companies for hot bets
  const filteredCompanies = selectedDomain === 'all'
    ? mockCompanies
    : mockCompanies.filter(c => c.liveEventSection === selectedDomain);

  const hotBetsCompanies = filteredCompanies.slice(0, 3);

  return (
    <PageLayout showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding}>
      {/* Carousel Section */}
      <section className="flex flex-col items-center py-4 md:py-8 px-0">
        <Swiper
          modules={[Autoplay, Pagination]}
          slidesPerView={1}
          spaceBetween={24}
          loop={true}
          autoplay={{ delay: 5000, disableOnInteraction: false }}
          pagination={{ clickable: true, el: '.custom-swiper-pagination' }}
          navigation={false}
          breakpoints={{
            640: { slidesPerView: 1 },
            768: { slidesPerView: 2 },
            1024: { slidesPerView: 3 },
          }}
          className="w-full max-w-[98vw] md:max-w-[90vw]"
        >
          {carouselDisplay.map((company, idx) => (
            <SwiperSlide key={company ? company.name : 'placeholder-' + idx}>
              {company ? (
                <div
                  key={company.name}
                  className="rounded-2xl p-3 bg-[linear-gradient(135deg,_#23282b_80%,_#444950_100%),_radial-gradient(circle_at_80%_20%,_rgba(255,255,255,0.13)_0%,_transparent_70%)] shadow-[0_8px_32px_0_rgba(40,199,111,0.18),_0_1.5px_8px_0_rgba(255,255,255,0.10)_inset] hover:shadow-[0_0_32px_8px_rgba(184,255,231,0.18)] hover:scale-[1.01] backdrop-blur-[6px] bg-opacity-95 transition-all duration-300 flex flex-col justify-between min-h-[80px] max-w-[750px] w-full mx-auto cursor-pointer relative overflow-hidden"
                  style={{ boxShadow: '0 4px 32px 0 rgba(184,255,231,0.18), 0 1.5px 8px 0 rgba(255,255,255,0.10) inset', }}
                  onClick={() => { setSelectedCompany(company); setModalOpen(true); }}
                >
                  <div className="absolute left-0 top-0 w-full h-full pointer-events-none z-10" style={{ background: 'linear-gradient(120deg,rgba(255,255,255,0.18)_0%,rgba(255,255,255,0.04)_60%,rgba(255,255,255,0.10)_100%)', mixBlendMode: 'screen', borderRadius: 'inherit' }} />
                  <span className="inline-block bg-[#28c76f] text-white text-xs font-bold px-4 py-1 rounded-full mb-4 w-fit">ACTIVE</span>
                  <div className="flex items-center justify-between mb-4">
                    <div>
                      <h3 className="text-2xl font-bold text-white mb-1">{company.name}</h3>
                      <div className="text-emerald-400 text-base font-medium">{company.role}</div>
                    </div>
                    <div className="flex items-center justify-center w-32 h-32">
                      <img
                        src={company.logo || '/logo192.png'}
                        alt={company.name + " logo"}
                        className="w-28 h-28 object-contain"
                        onError={e => { e.target.onerror = null; e.target.src = 'https://cdn-icons-png.flaticon.com/512/3135/3135715.png'; }}
                      />
                    </div>
                  </div>
                  <div className="flex flex-col gap-2 mb-4">
                    <div className="flex items-center gap-2 text-red-400 text-base font-medium">expires on: N/A</div>
                    <div className="flex items-center gap-2 text-gray-400 text-base">
                      Total Tokens: 0
                    </div>
                  </div>
                  <div className="flex justify-end mt-auto">
                    <button className="w-full bg-[#28c76f] hover:bg-[#22b455] text-white font-bold py-3 rounded-lg text-lg transition hover:shadow-md">Place Bet</button>
                  </div>
                </div>
              ) : (
                <div className="rounded-2xl p-3 bg-gray-800/40 min-h-[80px] max-w-[750px] w-full mx-auto flex items-center justify-center opacity-50 border-2 border-dashed border-gray-600" style={{height:'100%'}}>
                  <span className="text-gray-500">Coming Soon</span>
                </div>
              )}
            </SwiperSlide>
          ))}
        </Swiper>
        <div className="custom-swiper-pagination mt-4 flex justify-center gap-2"></div>
      </section>
      {/* Live Events and Hot Bets Section */}
      <section className="px-2 md:px-10 py-6 md:py-8">
        <div className="bg-[linear-gradient(to_top_right,_#181a1b_80%,_#28c76f_20%,_#181a1b_100%)] backdrop-blur-md border border-white/10 shadow-md rounded-xl p-6 md:p-10">
          <h2 className="text-2xl md:text-3xl font-bold text-white mb-4 md:mb-6 flex items-center gap-2">
            <span className="inline-flex items-center justify-center">
              <svg xmlns="http://www.w3.org/2000/svg" width="44" height="44" viewBox="0 0 24 24" fill="none" stroke="#28c76f" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-zap w-10 h-10"><polygon points="13 2 3 14 12 14 11 22 21 10 12 10 13 2"></polygon></svg>
            </span>
            Hot Bets
          </h2>
          <div className="flex gap-2 md:gap-4 mb-6 overflow-x-auto scrollbar-thin scrollbar-thumb-[#a3f7bf]/40 scrollbar-track-transparent">
            {filterOptions.map(opt => (
              <div className="flex flex-col items-center gap-1" key={opt.key}>
                <button
                  className={`rounded-full flex items-center justify-center focus:outline-none w-20 h-20 md:w-24 md:h-24 transition-all duration-200 text-green-400
                    ${selectedDomain === opt.key
                      ? 'bg-[#28c76f] text-white shadow-[0_0_32px_0_rgba(40,199,111,0.25)]'
                      : 'bg-[#181f1f] text-green-400 border border-[#28c76f]/30'}
                  `}
                  onClick={() => setSelectedDomain(opt.key)}
                  aria-label={opt.label}
                >
                  {opt.icon}
                </button>
                <span className={`text-lg font-medium transition-all duration-200 mt-2 ${selectedDomain === opt.key ? 'text-[#28c76f]' : 'text-white'}`}>{opt.label}</span>
              </div>
            ))}
          </div>
          <div className="flex flex-col md:flex-row gap-6 min-h-[220px]">
            {hotBetsCompanies.length === 0 ? (
              <div className="text-gray-400 text-lg py-12 w-full text-center flex items-center justify-center">No hot bets to display yet.</div>
            ) : (
              hotBetsCompanies.map((company, idx) => (
                <div
                  key={company.name}
                  className="bg-[#181f1f] rounded-2xl p-6 w-full md:w-[420px] h-[200px] flex flex-col justify-between shadow-lg relative border-2 border-[#28c76f]/80 transition-all duration-300 hover:shadow-2xl hover:scale-[1.03] hover:border-[#28c76f] cursor-pointer"
                  onClick={() => { setSelectedCompany(company); setModalOpen(true); }}
                >
                  <div className="flex justify-between items-start mb-2">
                    <span className="text-lg font-bold text-[#28c76f]">{company.name}</span>
                    <span className="bg-[#28c76f] text-white text-xs px-3 py-1 rounded-full font-semibold absolute top-6 right-6">{company.role || 'N/A'}</span>
                  </div>
                  <div className="flex-1 flex flex-col gap-1 mb-2">
                    {company.shortlist && company.shortlist.length > 0 ? company.shortlist.slice(0,2).map((candId, i) => {
                      const cand = mockCandidates.find(c => c.id === candId);
                      return cand ? (
                        <div className="flex items-center gap-3" key={cand.id}>
                          <span className="text-gray-200 font-medium">{cand.name}</span>
                          <span className="font-bold text-green-500 ml-auto">2.00x</span>
                          <span className="font-bold text-red-500">2.00x</span>
                        </div>
                      ) : null;
                    }) : (
                      <div className="flex items-center gap-3">
                        <span className="text-gray-200 font-medium">No candidates yet</span>
                      </div>
                    )}
                  </div>
                  <div className="flex justify-between items-end text-xs text-gray-400 mt-2">
                    <div className="flex items-center gap-1">N/A</div>
                    <div className="flex items-center gap-1">0 tokens</div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </section>
    </PageLayout>
  );
}
