import React, { useState, useEffect, useMemo } from "react";
import PageLayout from "./PageLayout";
import CompanyShortlistModal from './CompanyShortlistModal';
import axios from 'axios';
import { motion } from "framer-motion";

// This is your original hardcoded data for meta info, it can stay
const companiesMeta = [
  { name: "American Express", role: "Data Science", expires: "31/11/2024", tokens: 94036, domain: "data" },
  // ... and the rest of your companiesMeta array
];

const domainLabels = {
  quant: "Quant", sde: "SDE", product: "Product", data: "Data", core: "Core", misc: "Misc"
};

export default function ActiveBets({ user, showUserGuideModal, setShowUserGuideModal }) {
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [search, setSearch] = useState("");

  // State for live data from the API
  const [activeBetsData, setActiveBetsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // useEffect to fetch live data from the API
  useEffect(() => {
    const fetchActiveBets = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/bets/active');
        setActiveBetsData(res.data);
      } catch (err) {
        console.error("Failed to fetch active bets", err);
        setError("Could not load active bets.");
      } finally {
        setLoading(false);
      }
    };
    fetchActiveBets();
  }, []);

  // Your existing helper functions, now updated to use live API data
  function getTopCandidates(company) {
    const candidateTokenMap = {};
    activeBetsData.forEach(bet => {
      if (bet.companyEvent?._id === company.id && bet.candidate) {
        candidateTokenMap[bet.candidate._id] = (candidateTokenMap[bet.candidate._id] || 0) + (Number(bet.amount) || 0);
      }
    });
    return company.shortlist
      .map(cand => ({
        id: cand._id,
        name: cand.name,
        tokens: candidateTokenMap[cand._id] || 0
      }))
      .sort((a, b) => b.tokens - a.tokens)
      .slice(0, 3);
  }

  function getTotalTokens(company) {
    return activeBetsData.filter(bet => bet.companyEvent?._id === company.id)
      .reduce((sum, bet) => sum + (Number(bet.amount) || 0), 0);
  }

  function getCompanyMeta(company) {
    return companiesMeta.find(meta => meta.name.toLowerCase() === company.name.toLowerCase()) || {};
  }

  // This logic groups the flat list of bets by company for display
  const filteredCompanies = useMemo(() => {
    const companiesMap = new Map();
    activeBetsData.forEach(bet => {
      const company = bet.companyEvent;
      if (!company) return;

      if (!companiesMap.has(company._id)) {
        companiesMap.set(company._id, {
          ...company,
          id: company._id,
          name: company.companyName,
          role: company.jobProfile,
          shortlist: []
        });
      }
      if (bet.candidate) {
        const companyOnMap = companiesMap.get(company._id);
        if (!companyOnMap.shortlist.find(c => c._id === bet.candidate._id)) {
            companyOnMap.shortlist.push(bet.candidate);
        }
      }
    });
    let companies = Array.from(companiesMap.values());

    if (!search.trim()) return companies;
    const term = search.trim().toLowerCase();
    return companies.filter(c => 
      c.name.toLowerCase().includes(term) ||
      (c.role && c.role.toLowerCase().includes(term))
    );
  }, [search, activeBetsData]);

  if (loading) {
    return <PageLayout user={user}><div>Loading Active Bets...</div></PageLayout>;
  }
  if (error) {
    return <PageLayout user={user}><div>{error}</div></PageLayout>;
  }

  return (
    <PageLayout user={user} onUserGuide={() => setShowUserGuideModal(true)}>
      {/* --- YOUR FULL JSX DESIGN IS PRESERVED BELOW --- */}
      <div className="w-full flex flex-col items-center mb-6 mt-0">
        <motion.h1 initial={{ opacity: 0, y: -30 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5, ease: "easeOut" }} className="text-3xl sm:text-4xl font-bold text-[#28c76f] mb-1 mt-0 text-center">
          Active Bets
        </motion.h1>
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.2, duration: 0.5 }} className="text-base sm:text-lg text-gray-300 mb-6 text-center">
          Place your bets on upcoming placements
        </motion.div>
        <motion.input initial={{ opacity: 0, x: -40 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.3, duration: 0.5 }} type="text" value={search} onChange={e => setSearch(e.target.value)} placeholder="Search by companies or profiles..." className="w-full max-w-xs sm:max-w-xl px-4 sm:px-5 py-2 sm:py-3 bg-[#232b2b] border border-[#28c76f]/30 text-gray-200 text-base sm:text-lg focus:outline-none focus:ring-2 focus:ring-[#28c76f] mb-2 shadow" style={{ boxShadow: '0 2px 12px 0 rgba(40,199,111,0.05)', borderRadius: 0 }}/>
      </div>
      <motion.div className="w-full grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 px-0" initial="hidden" animate="visible" variants={{ visible: { transition: { staggerChildren: 0.15 } }, hidden: {} }}>
        {filteredCompanies.length === 0 ? (
          <motion.div variants={{ hidden: { opacity: 0, x: -40 }, visible: { opacity: 1, x: 0 } }} className="col-span-full flex flex-col items-center justify-center py-16">
            <span className="text-2xl text-[#28c76f] font-bold mb-2">No Active Bets Found</span>
            <span className="text-lg text-gray-400">Try searching or check back later! 🚀</span>
          </motion.div>
        ) : filteredCompanies.map(company => {
          const topCandidates = getTopCandidates(company);
          const totalTokens = getTotalTokens(company);
          const meta = getCompanyMeta(company);
          return (
            <motion.div key={company.id} variants={{ hidden: { opacity: 0, x: -40 }, visible: { opacity: 1, x: 0 } }} className="relative rounded-2xl border ...">
              {/* All your card JSX is preserved here */}
              {/* Header Row */}
              <div className="flex items-center justify-between px-4 pt-4 pb-1">
                <span className="text-2xl font-bold text-[#28c76f]">{company.name}</span>
                <span className="flex items-center ...">{company.shortlist.length}</span>
              </div>
              {/* Role, Expiry, Tokens */}
              <div className="px-4 flex ...">
                <span className="...">{domainLabels[meta.domain] || company.role}</span>
                <span className="...">{meta.expires || 'N/A'}</span>
                <span className="...">{totalTokens.toLocaleString()} tokens</span>
              </div>
              {/* Top Bettors Section */}
              <div className="px-4 pt-1 pb-1">
                <div className="font-semibold text-gray-200 mb-2">Top Bettors:</div>
                <div className="flex flex-col gap-2">
                  {topCandidates.length > 0 ? topCandidates.map(cand => (
                    <div key={cand.id} className="flex ...">
                      <span className="...">{cand.name}</span>
                      <div className="flex gap-2">
                        {/* Stakes logic will be added back later */}
                      </div>
                    </div>
                  )) : <div className="text-gray-400">No bets yet</div>}
                </div>
              </div>
              {/* View Details Link */}
              <div className="flex justify-end ...">
                <button className="..." onClick={() => { setSelectedCompany(company); setModalOpen(true); }}>
                  View Details
                </button>
              </div>
            </motion.div>
          );
        })}
      </motion.div>
      <CompanyShortlistModal
        open={modalOpen}
        onClose={() => setModalOpen(false)}
        company={selectedCompany}
        candidates={selectedCompany?.shortlist || []}
        // We will connect onSubmit later
        onSubmit={() => setModalOpen(false)}
      />
    </PageLayout>
  );
}
