import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import HomePage from "./HomePage";
import HeaderNav from "./HeaderNav";
import DropdownMenu from './DropdownMenu';
import MyBets from "./MyBets";
import ActiveBets from "./ActiveBets";
import PageLayout from "./PageLayout";
import Shortlists from "./Shortlists";
import ExpiredBets from "./ExpiredBets";
import Leaderboard from "./Leaderboard";
import AdminPanel from "./AdminPanel";
import PlacementData from "./PlacementData";
import Login from "./Login";
import Profile from "./Profile";

const defaultAnnouncements = [
  "Write a mail to us noreply@gmail.com",
  "Welcome to Jobsy! Enjoy betting responsibly.",
];

export default function App() {
  const [showAnnouncement, setShowAnnouncement] = useState(true);
  const [announcementIdx, setAnnouncementIdx] = useState(0);
  const [isSliding, setIsSliding] = useState(false);
  const announcements = defaultAnnouncements;
  return (
    <BrowserRouter>
      <HeaderNav />
      <Routes>
        <Route path="/" element={<HomePage showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding} />} />
        <Route path="/home" element={<HomePage showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding} />} />
        <Route path="/login" element={<Login />} />
        <Route path="/profile" element={<Profile showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding} />} />
        <Route path="/my-bets" element={<MyBets showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding} />} />
        <Route path="/active-bets" element={<ActiveBets showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding} />} />
        <Route path="/shortlists" element={<Shortlists showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding} />} />
        <Route path="/expired-bets" element={<ExpiredBets showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding} />} />
        <Route path="/leaderboard" element={<Leaderboard showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding} />} />
        <Route path="/admin-panel" element={<AdminPanel showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding} />} />
        <Route path="/placement-data" element={<PlacementData showAnnouncement={showAnnouncement} setShowAnnouncement={setShowAnnouncement} announcementIdx={announcementIdx} announcements={announcements} isSliding={isSliding} />} />
      </Routes>
    </BrowserRouter>
  );
}
