import React, { useState } from "react";
import { mockUsers } from './mockUsers';
import { mockBets, mockCompanies } from './mockData';

const user = {
  ...mockUsers[0],
  email: "valar.morghulis.got21@itbhu.ac.in",
  enrollment: "21075001",
  tokens: 103810,
  streak: 6,
  successRate: 90,
};

const stats = [
  {
    label: 'Total Bets',
    value: 14,
    icon: (
      <svg width="24" height="24" fill="none" stroke="#28c76f" strokeWidth="2.2" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10" /><circle cx="12" cy="12" r="3" /></svg>
    )
  },
  {
    label: 'Tokens',
    value: user.tokens.toLocaleString(),
    icon: (
      <svg width="24" height="24" fill="none" stroke="#28c76f" strokeWidth="2.2" viewBox="0 0 24 24"><rect x="4" y="4" width="16" height="16" rx="4" /><path d="M8 8h8v8H8z" /></svg>
    )
  },
  {
    label: 'Streak',
    value: user.streak + ' üî•',
    icon: (
      <svg width="24" height="24" fill="none" stroke="#28c76f" strokeWidth="2.2" viewBox="0 0 24 24"><polyline points="3 17 9 11 13 15 21 7" /></svg>
    )
  },
  {
    label: 'Success',
    value: user.successRate.toFixed(2) + '%',
    icon: (
      <svg width="24" height="24" fill="none" stroke="#28c76f" strokeWidth="2.2" viewBox="0 0 24 24"><rect x="4" y="4" width="16" height="16" rx="4" /><path d="M8 8h8v8H8z" /></svg>
    )
  },
];

export default function Profile() {
  const [tab, setTab] = useState('all');
  const bets = mockBets.filter(b => b.userId === user.enrollment || b.user === user.name);
  const filteredBets = tab === 'all' ? bets : bets.filter(b => b.status === tab);

  return (
    <div className="flex flex-col items-center min-h-screen py-4">
      <div className="w-full max-w-4xl mx-auto">
        {/* Profile Card */}
        <div className="rounded-2xl border border-[#28c76f] shadow-[0_2px_24px_0_rgba(40,199,111,0.10)] bg-[#181f1f] mb-8 p-8 flex flex-col md:flex-row items-center md:items-start gap-8">
          <div className="flex-shrink-0 flex items-center justify-center w-28 h-28 rounded-full bg-[#28c76f] text-4xl font-bold">
            {user.name.split(' ').map(n => n[0]).join('').toUpperCase()}
          </div>
          <div className="flex-1 flex flex-col justify-center">
            <div className="text-3xl font-bold text-[#28c76f] mb-2">{user.name}</div>
            <div className="text-lg text-gray-200 mb-1">{user.email}</div>
            <div className="text-lg text-gray-200 mb-1">Enrollment No: <span className="font-bold text-white">{user.enrollment}</span></div>
            <div className="text-gray-400 text-base mb-2">{user.tokens.toLocaleString()} Tokens</div>
          </div>
        </div>
        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          {stats.map((stat, idx) => (
            <div key={idx} className="rounded-2xl border border-[#28c76f] bg-[#181f1f] p-6 flex flex-col items-center shadow-[0_2px_24px_0_rgba(40,199,111,0.10)]">
              <span className="mb-2 text-[#28c76f] text-2xl">{stat.icon}</span>
              <span className="text-lg text-[#28c76f] mb-1">{stat.label}</span>
              <span className="text-2xl font-bold">{stat.value}</span>
            </div>
          ))}
        </div>
        {/* Betting History */}
        <div className="rounded-2xl border border-[#28c76f] bg-[#181f1f] p-8 mb-8 shadow-[0_2px_24px_0_rgba(40,199,111,0.10)]">
          <div className="text-2xl font-bold text-[#28c76f] mb-4">Betting History</div>
          <div className="flex gap-2 mb-6">
            <button className={`px-6 py-2 rounded-full font-semibold text-base transition-all duration-200 focus:outline-none ${tab === 'all' ? 'bg-[#28c76f] text-white' : 'bg-[#232b3a] text-[#6ee7b7]'}`} onClick={() => setTab('all')}>All</button>
            <button className={`px-6 py-2 rounded-full font-semibold text-base transition-all duration-200 focus:outline-none ${tab === 'active' ? 'bg-[#28c76f] text-white' : 'bg-[#232b3a] text-[#6ee7b7]'}`} onClick={() => setTab('active')}>Active</button>
            <button className={`px-6 py-2 rounded-full font-semibold text-base transition-all duration-200 focus:outline-none ${tab === 'expired' ? 'bg-[#28c76f] text-white' : 'bg-[#232b3a] text-[#6ee7b7]'}`} onClick={() => setTab('expired')}>Expired</button>
          </div>
          <div className="overflow-x-auto">
            <table className="min-w-full text-left text-white">
              <thead>
                <tr className="border-b border-[#28c76f]/30">
                  <th className="py-2 px-6 text-[#28c76f] font-semibold">Company</th>
                  <th className="py-2 px-6 text-[#28c76f] font-semibold">Type</th>
                  <th className="py-2 px-6 text-[#28c76f] font-semibold">Amount</th>
                  <th className="py-2 px-6 text-[#28c76f] font-semibold">Stake</th>
                  <th className="py-2 px-6 text-[#28c76f] font-semibold">Status</th>
                </tr>
              </thead>
              <tbody>
                {filteredBets.length === 0 ? (
                  <tr><td colSpan={5} className="text-center text-gray-400 py-8">No bets to show</td></tr>
                ) : (
                  filteredBets.map((bet, idx) => {
                    const company = mockCompanies.find(c => c.id === bet.companyId) || { name: bet.company || 'Unknown' };
                    return (
                      <tr key={idx} className="border-b border-[#28c76f]/10">
                        <td className="py-2 px-6">{company.name}</td>
                        <td className="py-2 px-6 text-xl">{bet.type === 'for' || bet.type === 'üëç' ? 'üëç' : 'üëé'}</td>
                        <td className="py-2 px-6">{bet.amount?.toLocaleString() || bet.amount}</td>
                        <td className="py-2 px-6">{bet.stake}</td>
                        <td className="py-2 px-6">
                          {bet.status === 'active' ? (
                            <span className="bg-[#28c76f] text-white px-3 py-1 rounded-full text-sm font-semibold">Active</span>
                          ) : (
                            <span className="bg-[#232b3a] text-gray-200 px-3 py-1 rounded-full text-sm font-semibold">Expired</span>
                          )}
                        </td>
                      </tr>
                    );
                  })
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}
