import React, { useState, useRef } from "react";
import { mockUsers } from './mockUsers';
import { useNavigate, useLocation } from "react-router-dom";
import DropdownMenu from './DropdownMenu';

const defaultUser = mockUsers[0]; // Jon Snow

export default function HeaderNav({ onUserGuide }) {
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef();
  const navigate = useNavigate();
  const location = useLocation();
  const user = defaultUser;

  return (
    <>
      {/* Header */}
      <header className="flex items-center justify-between px-4 md:px-10 py-4 md:py-6 bg-white/80 border-b border-[#e5f9ec] text-gray-900 relative shadow-sm">
        <div className="flex items-center gap-3 cursor-pointer" onClick={() => navigate('/home')}>
          <img src="/pngkey.com-poker-chips-png-594513.png" alt="Jobsy Logo" className="w-8 h-8 md:w-10 md:h-10" />
          <span className="text-2xl md:text-3xl font-bold text-[#28c76f]">Jobsy</span>
        </div>
        <div className="hidden md:flex items-center gap-6 relative">
          <div className="flex items-center gap-2 bg-[#093820] px-4 py-2 rounded-full">
            <svg width="20" height="20" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="text-[#28c76f]">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 1.343-3 3s1.343 3 3 3 3-1.343 3-3-1.343-3-3-3zm0 10c-4.418 0-8-1.79-8-4V6c0-2.21 3.582-4 8-4s8 1.79 8 4v8c0 2.21-3.582 4-8 4z" />
            </svg>
            <span className="font-semibold text-lg text-gray-900">{user.tokens?.toLocaleString() || '0'}</span>
          </div>
          <div className="relative" ref={menuRef}>
            <button className="w-10 h-10 bg-[#28c76f] rounded-full flex items-center justify-center font-bold text-lg text-white focus:outline-none focus:ring-2 focus:ring-[#28c76f]">
              {user.name?.split(' ').map(n => n[0]).join('').toUpperCase() || 'U'}
            </button>
          </div>
        </div>
      </header>
      {/* Navbar for desktop */}
      <nav className="hidden md:flex items-center gap-8 px-10 py-2 bg-[#181f1f] text-gray-100 text-base font-medium shadow-sm">
        <span className={`flex items-center gap-2 cursor-pointer px-3 py-2 hover:bg-[#28c76f] hover:border-[#28c76f] hover:border-2 ${location.pathname === '/shortlists' ? 'border-2 border-[#28c76f] bg-[#28c76f] text-[#181f1f]' : ''}`} onClick={() => navigate('/shortlists')}>See Shortlists</span>
        <span className={`flex items-center gap-2 cursor-pointer px-3 py-2 hover:bg-[#28c76f] hover:border-[#28c76f] hover:border-2 ${location.pathname === '/active-bets' ? 'border-2 border-[#28c76f] bg-[#28c76f] text-[#181f1f]' : ''}`} onClick={() => navigate('/active-bets')}>Active Bets</span>
        <span className={`flex items-center gap-2 cursor-pointer px-3 py-2 hover:bg-[#28c76f] hover:border-[#28c76f] hover:border-2 ${location.pathname === '/expired-bets' ? 'border-2 border-[#28c76f] bg-[#28c76f] text-[#181f1f]' : ''}`} onClick={() => navigate('/expired-bets')}>Expired Bets</span>
        <span className={`flex items-center gap-2 cursor-pointer px-3 py-2 hover:bg-[#28c76f] hover:border-[#28c76f] hover:border-2 ${location.pathname === '/leaderboard' ? 'border-2 border-[#28c76f] bg-[#28c76f] text-[#181f1f]' : ''}`} onClick={() => navigate('/leaderboard')}>Leaderboard</span>
        <span className={`flex items-center gap-2 cursor-pointer px-3 py-2 hover:bg-[#28c76f] hover:border-[#28c76f] hover:border-2 ${location.pathname === '/placement-data' ? 'border-2 border-[#28c76f] bg-[#28c76f] text-[#181f1f]' : ''}`} onClick={() => navigate('/placement-data')}>Placement Data</span>
        <span className={`flex items-center gap-2 cursor-pointer px-3 py-2 hover:bg-[#28c76f] hover:border-[#28c76f] hover:border-2 ${location.pathname === '/admin-panel' ? 'border-2 border-[#28c76f] bg-[#28c76f] text-[#181f1f]' : ''}`} onClick={() => navigate('/admin-panel')}>Admin Panel</span>
      </nav>
    </>
  );
}
