import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { mockUsers } from './mockUsers';

export default function Login() {
  const [tab, setTab] = useState('login');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const navigate = useNavigate();

  const handleLogin = e => {
    e.preventDefault();
    // In a real app, check credentials. Here, just navigate.
    navigate('/home');
  };

  const handleSignup = e => {
    e.preventDefault();
    // In a real app, create user. Here, just navigate.
    navigate('/home');
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-[60vh] bg-[#181f1f] text-white">
      <div className="bg-[#232b3a] rounded-2xl shadow-lg p-8 w-full max-w-md flex flex-col items-center">
        <div className="flex w-full mb-6">
          <button className={`flex-1 py-2 rounded-l-2xl font-bold text-lg ${tab === 'login' ? 'bg-[#28c76f] text-white' : 'bg-[#232b3a] text-gray-400'}`} onClick={() => setTab('login')}>Login</button>
          <button className={`flex-1 py-2 rounded-r-2xl font-bold text-lg ${tab === 'signup' ? 'bg-[#28c76f] text-white' : 'bg-[#232b3a] text-gray-400'}`} onClick={() => setTab('signup')}>Sign Up</button>
        </div>
        {tab === 'login' ? (
          <form className="w-full" onSubmit={handleLogin}>
            <input className="w-full mb-4 px-4 py-3 rounded-lg bg-[#181f1f] border border-[#28c76f]/40 text-white" type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required />
            <input className="w-full mb-6 px-4 py-3 rounded-lg bg-[#181f1f] border border-[#28c76f]/40 text-white" type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
            <button className="w-full bg-[#28c76f] hover:bg-[#22b36a] text-white font-bold py-3 rounded-full text-lg transition hover:shadow-md" type="submit">Login</button>
          </form>
        ) : (
          <form className="w-full" onSubmit={handleSignup}>
            <input className="w-full mb-4 px-4 py-3 rounded-lg bg-[#181f1f] border border-[#28c76f]/40 text-white" type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)} required />
            <input className="w-full mb-4 px-4 py-3 rounded-lg bg-[#181f1f] border border-[#28c76f]/40 text-white" type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required />
            <input className="w-full mb-6 px-4 py-3 rounded-lg bg-[#181f1f] border border-[#28c76f]/40 text-white" type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
            <button className="w-full bg-[#28c76f] hover:bg-[#22b36a] text-white font-bold py-3 rounded-full text-lg transition hover:shadow-md" type="submit">Sign Up</button>
          </form>
        )}
      </div>
    </div>
  );
}
