import React, { useState, useRef, useEffect, useMemo } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Autoplay, Pagination } from "swiper/modules";
import "swiper/css";
import "swiper/css/pagination";
import './index.css';
import { useNavigate } from "react-router-dom";
import DropdownMenu from './DropdownMenu';
import PageLayout from "./PageLayout";
import CompanyShortlistModal from './CompanyShortlistModal';
import { motion } from "framer-motion";
import { useAuth } from './context/AuthContext';
import api from './services/api';

const filterOptions = [
  {
    key: 'all', label: 'All',
    icon: (selected) => (
      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke={selected ? '#fff' : '#22b36a'} strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-play h-5 w-5"><polygon points="6 3 20 12 6 21 6 3"></polygon></svg>
    )
  },
  {
    key: 'quant', label: 'Quant',
    icon: (selected) => (
      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke={selected ? '#fff' : '#22b36a'} strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-calculator h-5 w-5"><rect x="4" y="2" width="16" height="20" rx="2"/><rect x="8" y="6" width="8" height="2"/><line x1="9" y1="12" x2="9" y2="12"/><line x1="15" y1="12" x2="15" y2="12"/><line x1="9" y1="16" x2="9" y2="16"/><line x1="15" y1="16" x2="15" y2="16"/></svg>
    )
  },
  {
    key: 'sde', label: 'SDE',
    icon: (selected) => (
      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke={selected ? '#fff' : '#22b36a'} strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-code h-5 w-5"><polyline points="16 18 22 12 16 6"/><polyline points="8 6 2 12 8 18"/></svg>
    )
  },
  {
    key: 'product', label: 'Product',
    icon: (selected) => (
      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke={selected ? '#fff' : '#22b36a'} strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-box h-5 w-5"><path d="M21 16V8a2 2 0 0 0-1-1.73l-7-4a2 2 0 0 0-2 0l-7 4A2 2 0 0 0 3 8v8a2 2 0 0 0 1 1.73l7 4a2 2 0 0 0 2 0l7-4A2 2 0 0 0 21 16z"/><polyline points="3.27 6.96 12 12.01 20.73 6.96"/><line x1="12" y1="22.08" x2="12" y2="12"/></svg>
    )
  },
  {
    key: 'data', label: 'Data',
    icon: (selected) => (
      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke={selected ? '#fff' : '#22b36a'} strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-database h-5 w-5"><ellipse cx="12" cy="5" rx="9" ry="3"/><path d="M3 5v14c0 1.66 4.03 3 9 3s9-1.34 9-3V5"/><path d="M3 12c0 1.66 4.03 3 9 3s9-1.34 9-3"/></svg>
    )
  },
  {
    key: 'core', label: 'Core',
    icon: (selected) => (
      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke={selected ? '#fff' : '#22b36a'} strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-cpu h-5 w-5"><rect x="4" y="4" width="16" height="16" rx="2"/><rect x="9" y="9" width="6" height="6"/><path d="M15 2v2"/><path d="M15 20v2"/><path d="M2 15h2"/><path d="M2 9h2"/><path d="M20 15h2"/><path d="M20 9h2"/><path d="M9 2v2"/><path d="M9 20v2"/></svg>
    )
  },
  {
    key: 'misc', label: 'Misc',
    icon: (selected) => (
      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke={selected ? '#fff' : '#22b36a'} strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-more-horizontal h-5 w-5"><circle cx="12" cy="12" r="1"/><circle cx="19" cy="12" r="1"/><circle cx="5" cy="12" r="1"/></svg>
    )
  },
];

export default function HomePage({ showUserGuideModal, setShowUserGuideModal, userGuideContent }) {
  const { user, updateUser } = useAuth();
  const [menuOpen, setMenuOpen] = useState(false);
  const [showWelcomeModal, setShowWelcomeModal] = useState(false);
  const [selectedDomain, setSelectedDomain] = useState('all');
  const [showAnnouncement, setShowAnnouncement] = useState(true);
  const [announcementIdx, setAnnouncementIdx] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  // API Data States
  const [companies, setCompanies] = useState([]);
  const [candidates, setCandidates] = useState([]);
  const [bets, setBets] = useState([]);
  const [myBets, setMyBets] = useState([]);
  
  const announcements = [
    'Placement Season Surprise: Bet Big, Win Bigger!',
    'Write a mail to us noreply@gmail.com',
    'All the best for placements!'
  ];
  
  const navigate = useNavigate();
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCompany, setSelectedCompany] = useState(null);

  // Load data from API
  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        setError('');
        
        // Test API connection first
        try {
          const testResponse = await fetch('http://localhost:5000/api/health');
          if (!testResponse.ok) {
            throw new Error(`Backend server responded with status: ${testResponse.status}`);
          }
          const healthData = await testResponse.json();
          console.log('Backend health check:', healthData);
        } catch (testError) {
          console.error('Backend connection test failed:', testError);
          setError('Cannot connect to backend server. Please make sure it\'s running on port 5000.');
          setLoading(false);
          return;
        }
        
        // Load data with individual error handling
        let companiesData = [];
        let candidatesData = [];
        let betsData = [];
        let myBetsData = [];

        try {
          companiesData = await api.getCompanies();
        } catch (error) {
          console.error('Error loading companies:', error);
          // Continue with empty array
        }

        try {
          candidatesData = await api.getCandidates();
        } catch (error) {
          console.error('Error loading candidates:', error);
          // Continue with empty array
        }

        try {
          betsData = await api.getBets();
        } catch (error) {
          console.error('Error loading bets:', error);
          // Continue with empty array
        }

        try {
          myBetsData = await api.getMyBets();
        } catch (error) {
          console.error('Error loading my bets:', error);
          // Continue with empty array
        }
        
        setCompanies(companiesData);
        setCandidates(candidatesData);
        setBets(betsData);
        setMyBets(myBetsData);

        // Check if we got any data
        if (companiesData.length === 0 && candidatesData.length === 0 && betsData.length === 0) {
          setError('No data available. The database might be empty. Please check if data has been seeded.');
        }
      } catch (error) {
        console.error('Error loading data:', error);
        setError('Failed to load data. Please check your connection and try again.');
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  useEffect(() => {
    if (!showAnnouncement) return;
    const interval = setInterval(() => {
      setAnnouncementIdx(idx => (idx + 1) % announcements.length);
    }, 4000);
    return () => clearInterval(interval);
  }, [showAnnouncement]);

  // Add this handler inside HomePage
  async function handleModalSubmit(betsToSubmit, totalBet) {
    try {
      // Submit bets to API
      const betPromises = betsToSubmit.map(bet => 
        api.placeBet({
          candidateId: bet.candidateId,
          companyId: bet.companyId,
          type: bet.type,
          amount: bet.amount
        })
      );
      
      await Promise.all(betPromises);
      
      // Update user tokens
      const updatedUser = { ...user, tokens: user.tokens - totalBet };
      updateUser(updatedUser);
      
      // Refresh bets data
      const [newBets, newMyBets] = await Promise.all([
        api.getBets(),
        api.getMyBets()
      ]);
      setBets(newBets);
      setMyBets(newMyBets);
      
      setModalOpen(false);
    } catch (error) {
      console.error('Error placing bets:', error);
      setError('Failed to place bets. Please try again.');
    }
  }

  // Helper: check if company has results declared
  const hasResultsDeclared = (company) => {
    return company.candidates && company.candidates.some(cand => cand.result !== 'awaited');
  };

  // Filter companies for Live Events box (only show companies where results are NOT declared)
  const filteredCompanies = selectedDomain === 'all' 
    ? companies.filter(c => !hasResultsDeclared(c))
    : companies.filter(c => c.liveEventSection === selectedDomain && !hasResultsDeclared(c));

  // Filter companies for Hot Bets (top 3 by tokens, filtered by selected domain, only active companies)
  const hotBetsCompanies = (selectedDomain === 'all'
    ? companies.filter(c => !hasResultsDeclared(c))
    : companies.filter(c => c.liveEventSection === selectedDomain && !hasResultsDeclared(c)))
    .sort((a, b) => {
      // Calculate total tokens for each company
      const totalTokensA = bets.filter(bet => bet.companyId === a._id)
        .reduce((sum, bet) => sum + (Number(bet.amount) || 0), 0);
      const totalTokensB = bets.filter(bet => bet.companyId === b._id)
        .reduce((sum, bet) => sum + (Number(bet.amount) || 0), 0);
      return totalTokensB - totalTokensA;
    })
    .slice(0, 3);

  // Persistent, bet-driven stakes for each company
  const [companyStakes, setCompanyStakes] = useState({});

  useEffect(() => {
    // Recalculate stakes whenever bets change
    const newStakes = {};
    companies.forEach(company => {
      const shortlistedIds = company.shortlist || [];
      shortlistedIds.forEach(cid => {
        // Total tokens bet on this candidate
        const t_i = bets.filter(bet => bet.companyId === company._id && bet.candidateId === cid)
          .reduce((sum, bet) => sum + (Number(bet.amount) || 0), 0);
        // Improved odds formula: only change for candidate being bet on
        const baseFor = 2.0;
        const minFor = 1.05;
        const maxFor = 10.0;
        const k = 0.0005;
        let forStake = baseFor;
        let againstStake = baseFor;
        if (t_i > 0) {
          forStake = Math.max(minFor, baseFor - k * t_i);
          againstStake = Math.min(maxFor, baseFor + k * t_i);
        }
        if (!newStakes[company._id]) newStakes[company._id] = {};
        newStakes[company._id][cid] = {
          for: forStake.toFixed(2),
          against: againstStake.toFixed(2)
        };
      });
    });
    setCompanyStakes(newStakes);
  }, [bets, companies]);

  // Calculate top 8 companies by total tokens bet
  const topCompanyIds = useMemo(() => {
    const totals = {};
    bets.forEach(bet => {
      if (!totals[bet.companyId]) totals[bet.companyId] = 0;
      totals[bet.companyId] += Number(bet.amount) || 0;
    });
    // Sort company ids by total tokens bet, descending
    return Object.entries(totals)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 8)
      .map(([id]) => id);
  }, [bets]);

  // Use admin-selected companies for the carousel (for now, use top companies)
  const carouselCompanies = companies
    .filter(c => topCompanyIds.includes(c._id) && !hasResultsDeclared(c))
    .slice(0, 5);

  // Helper to close menu on outside click
  const menuRef = useRef();
  React.useEffect(() => {
    function handleClick(e) {
      if (menuOpen && menuRef.current && !menuRef.current.contains(e.target)) {
        setMenuOpen(false);
      }
    }
    document.addEventListener('mousedown', handleClick);
    return () => document.removeEventListener('mousedown', handleClick);
  }, [menuOpen]);

  function handleLogout() {
    setMenuOpen(false);
    // Clear user state if needed
    navigate('/');
  }

  return (
    <>
      <PageLayout user={user} onUserGuide={() => setShowUserGuideModal(true)}>
        {/* Loading overlay */}
        {loading && (
          <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/30">
            <div className="text-center">
              <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500 mx-auto"></div>
              <p className="mt-4 text-gray-600">Loading...</p>
            </div>
          </div>
        )}
        {/* Error banner */}
        {error && (
          <div className="w-full bg-red-100 text-red-700 px-4 py-3 rounded mb-4 text-center font-semibold">
            {error}
          </div>
        )}
        <motion.div initial={{ opacity: 0, y: 40 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.7, ease: 'easeOut' }}>
          {/* Carousel Section */}
          <motion.section className="flex flex-col items-center py-4 md:py-8 px-0">
            {carouselCompanies.length === 0 ? (
              <div className="text-gray-400 text-lg py-12">No companies to display yet.</div>
            ) : (
              <>
                <Swiper
                  modules={[Autoplay, Pagination]}
                  slidesPerView={1}
                  spaceBetween={24}
                  loop={true}
                  autoplay={{ delay: 5000, disableOnInteraction: false }}
                  pagination={{ clickable: true, el: '.custom-swiper-pagination' }}
                  navigation={false}
                  breakpoints={{
                    640: { slidesPerView: 1 },
                    768: { slidesPerView: 2 },
                    1024: { slidesPerView: 3 },
                  }}
                  className="w-full max-w-[98vw] md:max-w-[90vw]"
                >
                  {carouselCompanies.map((company, idx) => {
                    // Calculate total tokens for this company
                    const totalTokens = bets.filter(bet => bet.companyId === company._id)
                      .reduce((sum, bet) => sum + (Number(bet.amount) || 0), 0);
                    return (
                      <SwiperSlide key={company.name}>
                        <motion.div
                          key={company.name}
                          initial={{ opacity: 0, x: -40 }}
                          whileInView={{ opacity: 1, x: 0 }}
                          viewport={{ once: true, amount: 0.3 }}
                          transition={{ duration: 0.6, delay: idx * 0.15, ease: "easeOut" }}
                          className="rounded-2xl p-3 bg-[linear-gradient(135deg,_#23282b_80%,_#444950_100%),_radial-gradient(circle_at_80%_20%,_rgba(255,255,255,0.13)_0%,_transparent_70%)] shadow-[0_8px_32px_0_rgba(40,199,111,0.18),_0_1.5px_8px_0_rgba(255,255,255,0.10)_inset] hover:shadow-[0_0_32px_8px_rgba(184,255,231,0.18)] hover:scale-[1.01] backdrop-blur-[6px] bg-opacity-95 transition-all duration-300 flex flex-col justify-between min-h-[80px] max-w-[750px] w-full mx-auto cursor-pointer relative overflow-hidden"
                          style={{ boxShadow: '0 4px 32px 0 rgba(184,255,231,0.18), 0 1.5px 8px 0 rgba(255,255,255,0.10) inset', }}
                          onClick={() => { setSelectedCompany(company); setModalOpen(true); }}
                        >
                          {/* Glossy highlight overlay */}
                          <div className="absolute left-0 top-0 w-full h-full pointer-events-none z-10" style={{ background: 'linear-gradient(120deg,rgba(255,255,255,0.18)_0%,rgba(255,255,255,0.04)_60%,rgba(255,255,255,0.10)_100%)', mixBlendMode: 'screen', borderRadius: 'inherit' }} />
                          <span className="inline-block bg-[#28c76f] text-white text-xs font-bold px-4 py-1 rounded-full mb-4 w-fit">ACTIVE</span>
                          <div className="flex items-center justify-between mb-4">
                            <div>
                              <h3 className="text-2xl font-bold text-white mb-1">{company.name}</h3>
                              <div className="text-emerald-400 text-base font-medium">{company.role}</div>
                            </div>
                            <div className="flex items-center justify-center w-32 h-32">
                              <img
                                src={company.logo || '/logo192.png'}
                                alt={company.name + " logo"}
                                className="w-28 h-28 object-contain"
                                onError={e => { e.target.onerror = null; e.target.src = 'https://cdn-icons-png.flaticon.com/512/3135/3135715.png'; }}
                              />
                            </div>
                          </div>
                          <div className="flex flex-col gap-2 mb-4">
                            <div className="flex items-center gap-2 text-red-400 text-base font-medium"><svg width="20" height="20" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" /></svg> expires on: {company.expiresOn || 'N/A'}</div>
                            <div className="flex items-center gap-2 text-gray-400 text-base">
                              <svg width="20" height="20" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 17v-2a4 4 0 014-4h10a4 4 0 014 4v2" /></svg>
                              Total Tokens: {totalTokens.toLocaleString()}
                            </div>
                          </div>
                          <div className="flex justify-end mt-auto">
                            <button className="w-full bg-[#28c76f] hover:bg-[#22b455] text-white font-bold py-3 rounded-lg text-lg transition hover:shadow-md">Place Bet</button>
                          </div>
                        </motion.div>
                      </SwiperSlide>
                    );
                  })}
                </Swiper>
                <div className="custom-swiper-pagination mt-4 flex justify-center gap-2"></div>
              </>
            )}
          </motion.section>
          {/* Live Events and Hot Bets Section */}
          <motion.section className="px-2 md:px-10 py-6 md:py-8">
            <motion.div className="bg-[linear-gradient(to_top_right,_#181a1b_80%,_#28c76f_20%,_#181a1b_100%)] backdrop-blur-md border border-white/10 shadow-md rounded-xl p-6 md:p-10">
              <h2 className="text-2xl md:text-3xl font-bold text-white mb-4 md:mb-6 flex items-center gap-2">
                <span className="inline-flex items-center justify-center">
                  <svg xmlns="http://www.w3.org/2000/svg" width="44" height="44" viewBox="0 0 24 24" fill="none" stroke="#28c76f" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-zap w-10 h-10"><polygon points="13 2 3 14 12 14 11 22 21 10 12 10 13 2"></polygon></svg>
                </span>
                Live Events
              </h2>
              <div className="flex gap-2 md:gap-4 mb-6 overflow-x-auto scrollbar-thin scrollbar-thumb-[#a3f7bf]/40 scrollbar-track-transparent">
                {filterOptions.map(opt => (
                  <div className="flex flex-col items-center gap-1" key={opt.key}>
                    <button
                      className={`rounded-full flex items-center justify-center focus:outline-none w-10 h-10 md:w-12 md:h-12 transition-all duration-200
                        ${selectedDomain === opt.key
                          ? 'bg-[#28c76f] shadow-[0_0_32px_0_rgba(40,199,111,0.25)]'
                          : 'bg-[#0a0f0c]'}
                      `}
                      onClick={() => setSelectedDomain(opt.key)}
                      aria-label={opt.label}
                    >
                      {opt.icon(selectedDomain === opt.key)}
                    </button>
                    <span className={`text-sm md:text-base font-medium transition-all duration-200 mt-2 text-white`}>{opt.label}</span>
                  </div>
                ))}
              </div>
              <div className="flex flex-col md:flex-row gap-6">
                {hotBetsCompanies.length === 0 ? (
                  <div className="text-gray-400 text-lg py-12">No hot bets to display yet.</div>
                ) : (
                  hotBetsCompanies.map((company, idx) => (
                    <motion.div
                      key={company.name}
                      initial={{ opacity: 0, x: -40 }}
                      whileInView={{ opacity: 1, x: 0 }}
                      viewport={{ once: true, amount: 0.3 }}
                      transition={{ duration: 0.6, delay: idx * 0.15, ease: "easeOut" }}
                      className="bg-[#181f1f] rounded-2xl p-6 w-full md:w-[420px] h-[200px] flex flex-col justify-between shadow-lg relative border-2 border-[#28c76f]/80 transition-all duration-300 hover:shadow-2xl hover:scale-[1.03] hover:border-[#28c76f] cursor-pointer"
                      onClick={() => { setSelectedCompany({
                        ...company,
                        role: company.role || 'N/A',
                        logo: company.logo || '/logo192.png',
                        expires: company.expiresOn || 'N/A',
                        tokens: bets.filter(bet => bet.companyId === company._id).reduce((sum, bet) => sum + (Number(bet.amount) || 0), 0),
                        domain: company.liveEventSection || 'misc',
                      }); setModalOpen(true); }}
                    >
                      <div className="flex justify-between items-start mb-2">
                        <span className="text-lg font-bold text-[#28c76f]">{company.name}</span>
                        <span className="bg-[#28c76f] text-white text-xs px-3 py-1 rounded-full font-semibold absolute top-6 right-6">{company.role || 'N/A'}</span>
                      </div>
                      <div className="flex-1 flex flex-col gap-1 mb-2">
                        {company.candidates && company.candidates.length > 0 ? company.candidates.map((cand, i) => (
                          <div className="flex items-center gap-3" key={cand._id}>
                            <span className="text-gray-200 font-medium">{cand.name}</span>
                            <span className="font-bold text-green-500 ml-auto">{companyStakes[company._id]?.[cand._id]?.for || '2.00'}x</span>
                            <span className="font-bold text-red-500">{companyStakes[company._id]?.[cand._id]?.against || '2.00'}x</span>
                          </div>
                        )) : (
                          <div className="flex items-center gap-3">
                            <span className="text-gray-200 font-medium">No candidates yet</span>
                          </div>
                        )}
                      </div>
                      <div className="flex justify-between items-end text-xs text-gray-400 mt-2">
                        <div className="flex items-center gap-1"><svg width="16" height="16" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" /></svg> {company.expiresOn || 'N/A'}</div>
                        <div className="flex items-center gap-1"><svg width="16" height="16" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 17v-2a4 4 0 014-4h10a4 4 0 014 4v2" /></svg> {bets.filter(bet => bet.companyId === company._id).reduce((sum, bet) => sum + (Number(bet.amount) || 0), 0).toLocaleString()} tokens</div>
                      </div>
                    </motion.div>
                  ))
                )}
              </div>
            </motion.div>
          </motion.section>
        </motion.div>
      </PageLayout>
      
      {showUserGuideModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/40 backdrop-blur-sm">
          <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-2xl w-full relative overflow-y-auto max-h-[90vh]">
            <button className="absolute top-4 right-4 text-gray-400 hover:text-gray-700 text-2xl" onClick={() => setShowUserGuideModal(false)}>&times;</button>
            {userGuideContent}
            <button className="mt-8 w-full bg-[#28c76f] hover:bg-[#22b36a] text-white font-bold py-3 rounded-full text-lg transition hover:shadow-md" onClick={() => setShowUserGuideModal(false)}>Get Started</button>
          </div>
        </div>
      )}
      
      <CompanyShortlistModal
        open={modalOpen}
        onClose={() => setModalOpen(false)}
        company={selectedCompany}
        candidates={candidates}
        bets={bets}
        onSubmit={handleModalSubmit}
        stakes={selectedCompany ? companyStakes[selectedCompany._id] : {}}
      />
    </>
  );
}

function getInitials(name) {
  return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
}
