import React, { useState, useEffect, useMemo } from "react";
import PageLayout from "./PageLayout";
import CompanyShortlistModal from './CompanyShortlistModal';
import { motion } from "framer-motion";
import axios from 'axios';

export default function ActiveBets({ user }) {
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [search, setSearch] = useState("");

  // --- State for API Data ---
  const [activeBetsData, setActiveBetsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // --- Fetch Data from API ---
  useEffect(() => {
    const fetchActiveBets = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/bets/active');
        setActiveBetsData(res.data);
      } catch (err) {
        console.error("Failed to fetch active bets", err);
        setError("Could not load active bets.");
      } finally {
        setLoading(false);
      }
    };
    fetchActiveBets();
  }, []);

  // This logic groups your flat list of bets by company for display
  const filteredEvents = useMemo(() => {
    if (!activeBetsData) return [];

    const companiesMap = new Map();
    activeBetsData.forEach(bet => {
      const company = bet.companyEvent;
      if (!company) return;

      if (!companiesMap.has(company._id)) {
        companiesMap.set(company._id, {
          ...company,
          id: company._id,
          name: company.companyName,
          role: company.jobProfile,
          // Store candidates that have been bet on for this company
          shortlist: [bet.candidate] 
        });
      } else {
        // Add more candidates to the list if the bet is for a new candidate
        const existingCompany = companiesMap.get(company._id);
        if (!existingCompany.shortlist.find(c => c._id === bet.candidate._id)) {
          existingCompany.shortlist.push(bet.candidate);
        }
      }
    });

    let companies = Array.from(companiesMap.values());

    if (!search.trim()) return companies;
    const term = search.trim().toLowerCase();
    return companies.filter(c => 
      c.name.toLowerCase().includes(term) ||
      (c.role && c.role.toLowerCase().includes(term))
    );
  }, [search, activeBetsData]);

  // We will build the logic for placing bets and calculating stakes later
  function handleModalSubmit(betsToSubmit, totalBet) {
    console.log("Submitting Bets:", betsToSubmit, "Total:", totalBet);
    setModalOpen(false);
  }

  if (loading) {
    return <PageLayout user={user}><div>Loading Active Bets...</div></PageLayout>;
  }
  if (error) {
    return <PageLayout user={user}><div>{error}</div></PageLayout>;
  }

  return (
    <PageLayout user={user}>
      {/* Your existing Heading and Search Bar JSX */}
      <div className="w-full flex flex-col items-center mb-6 mt-0">
        <motion.h1 initial={{ opacity: 0, y: -30 }} animate={{ opacity: 1, y: 0 }} className="text-3xl sm:text-4xl ...">
          Active Bets
        </motion.h1>
        {/* ... your search input ... */}
      </div>

      {/* Your existing grid for displaying the cards */}
      <motion.div className="w-full grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 px-0">
        {filteredEvents.length === 0 ? (
          <div className="col-span-full ...">No active bets found.</div>
        ) : filteredEvents.map(company => (
          // This is a simplified card. You will need to paste your full card design here.
          // IMPORTANT: Use the correct field names from the API data.
          <motion.div key={company._id} className="relative rounded-2xl ...">
            <div className="flex items-center justify-between px-4 pt-4 pb-1">
              <span className="text-2xl font-bold text-[#28c76f]">{company.name}</span>
              <span className="...">{company.shortlist.length}</span>
            </div>
            <div className="px-4 ...">
                <span>{company.role}</span>
            </div>
            <div className="px-4 pt-1 pb-1">
                <div className="font-semibold text-gray-200 mb-2">Top Bettors:</div>
                {/* Map over company.shortlist to display candidates */}
                {company.shortlist.slice(0,3).map(candidate => (
                    <div key={candidate._id} className="flex ...">
                        <span>{candidate.name}</span>
                    </div>
                ))}
            </div>
            <div className="flex justify-end px-4 pb-3 pt-1">
                <button onClick={() => { setSelectedCompany(company); setModalOpen(true); }}>
                    View Details
                </button>
            </div>
          </motion.div>
        ))}
      </motion.div>

      {/* Your existing CompanyShortlistModal */}
      <CompanyShortlistModal
        open={modalOpen}
        onClose={() => setModalOpen(false)}
        company={selectedCompany}
        candidates={selectedCompany?.shortlist || []} // Pass the correct candidates
        bets={[]} // Pass empty array for now
        onSubmit={handleModalSubmit}
        stakes={{}} // Pass empty object for now
      />
    </PageLayout>
  );
}
