import React from "react";
import { useNavigate } from "react-router-dom";
import { mockUsers } from './mockUsers';

export default function Login() {
  const navigate = useNavigate();
  const handleLogin = () => {
    // In a real app, set user in context/state. Here, just navigate.
    navigate('/home');
  };
  const user = mockUsers[0];
  return (
    <div className="flex flex-col items-center justify-center min-h-[60vh] bg-[#181f1f] text-white">
      <div className="bg-[#232b3a] rounded-2xl shadow-lg p-8 w-full max-w-md flex flex-col items-center">
        <div className="w-24 h-24 rounded-full bg-[#28c76f] flex items-center justify-center text-4xl font-bold mb-4">
          {user.name.split(' ').map(n => n[0]).join('').toUpperCase()}
        </div>
        <div className="text-2xl font-bold mb-2">Welcome to Jobsy</div>
        <div className="text-[#28c76f] text-lg mb-2">Login as {user.name}</div>
        <button className="mt-6 w-full bg-[#28c76f] hover:bg-[#22b36a] text-white font-bold py-3 rounded-full text-lg transition hover:shadow-md" onClick={handleLogin}>Login</button>
      </div>
    </div>
  );
}
